      function open_to_append_at_pos(dir,name,pos) result(un)
        implicit none
        character(len=*),intent(in) :: dir,name
        integer,intent(in) :: pos
        integer :: un
        integer :: i
        un = open_to_append(dir,name)
        rewind(un)
        do i=1,pos-1; read(un,*); enddo
      end function

      function get_n_lines_of_file(dir,name) result(n_lines)
        implicit none
        character(len=*),intent(in) :: dir,name
        integer(li) :: n_lines
        integer :: un,stat
        logical :: not_EOF
        un = open_to_read(dir,name)
        n_lines = 0
        not_EOF = .true.
        do while (not_EOF)
          read(un,*,iostat=stat)
          if (stat .lt. 0) then
            not_EOF = .false.
          else
            n_lines = n_lines + 1
          endif
        enddo
        close(un)
      end function

