         write(un,*) 'Error: newtonT2 not converged, gracefully handling.'
         write(un,*) 'fbeta = ',fbeta
         write(un,*) 'beta = ',beta
         write(un,*) 'tol = ',tol
         write(un,*) 'cond_1 = ',is_nan(beta)
         write(un,*) 'cond_2 = ',(abs(fbeta).gt.tol)
