          else;                 call init(f,f1)
          endif
        endif
      end subroutine

      subroutine make_dir_DM(d)
        implicit none
        character(len=*),intent(in) :: d
        logical :: ex
        inquire (file=d, EXIST=ex)
        if (.not.ex) then
          call system('mkdir ' // d )
          write(*,*) 'Directory ' // d // ' created.'
        else
          write(*,*) 'Directory ' // d // ' already exists.'
        endif
      end subroutine

      subroutine make_dir_quiet_DM(d)
