      subroutine pop_element_array(A)
        implicit none
        type(array),intent(inout) :: A
        type(array) :: temp
#ifdef _DEBUG_ARRAY_
        call insist_allocated(A,'pop_element_array')
#endif
        if (A%N.eq.1) then
          call delete(A)
        else
          call init(temp,A)
          call init(A,(/temp%f(1:temp%N-1)/),temp%N-1)
          call delete(temp)
