         DL%volume_ID = 5+dir
         call init_CC_N_along(DL,'init_Edge_DL')
         select case (dir)
         case (1); DL%N_eye = (/0,1,1/); DL%CC_eye = (/1,0,0/)
         case (2); DL%N_eye = (/1,0,1/); DL%CC_eye = (/0,1,0/)
         case (3); DL%N_eye = (/1,1,0/); DL%CC_eye = (/0,0,1/)
