       ! subroutine initVortex(u,v,w,g,vdir,vsign,dir)
       !   implicit none
       !   real(cp),dimension(:,:,:),intent(inout) :: u,v,w
       !   type(grid),intent(in) :: g
       !   integer,intent(in) :: vdir,vsign
       !   character(len=*),intent(in) :: dir
