       subroutine init_probe(p,tec_dir,tec_name,restart,simple,TMP)
         implicit none
         type(probe),intent(inout) :: p
         character(len=*),intent(in) :: tec_dir,tec_name
         logical,intent(in) :: restart,simple
         type(time_marching_params),intent(in) :: TMP
