        function symmetry_error_x_GF(U) result(F)
          implicit none
          type(grid_field),intent(in) :: U
          type(grid_field) :: temp
          real(cp) :: F
          call init(temp,U)
          call assign(temp,U)
          call symmetry_local_x(temp)
          F = amax(temp)
          call delete(temp)
        end function

        function symmetry_error_y_GF(U) result(F)
          implicit none
          type(grid_field),intent(in) :: U
          type(grid_field) :: temp
