          call add(U,temp)
          call delete(temp)
        end subroutine

        subroutine symmetry_local_z_GF(U)
          implicit none
          type(grid_field),intent(inout) :: U
          type(grid_field) :: temp
          integer :: i
          call init(temp,U)
          do i=1,U%s(3); call assign_plane_z(temp,U,i,U%s(3)-i+1); enddo
          call multiply(temp,-1.0_cp)
          call add(U,temp)
          call delete(temp)
