       subroutine addGrid(m,g)
         implicit none
         type(mesh),intent(inout) :: m
         type(grid),intent(in) :: g
         type(mesh) :: temp
         integer :: i
         if (allocated(m%B)) then
           if (m%s.lt.1) stop 'Error: allocated mesh but size<1 in addGrid mesh.f90'
           call init(temp,m)
           call delete(m)
           m%s = temp%s + 1
           allocate(m%B(m%s))
           do i=1,temp%s; call init(m%B(i),temp%B(i)); enddo
           call init(m%B(m%s),g)
           call delete(temp)
         else
           allocate(m%B(1))
           call init(m%B(1),g)
           m%s = 1
         endif
         call init_props(m)
         call insist_allocated_mesh(m,'addGrid')
         m%defined = .true.
       end subroutine

       subroutine remove_stitches(m)
         implicit none
         type(mesh),intent(inout) :: m
         integer :: i
