        subroutine insist_collocated_VF(f,caller)
          implicit none
          type(VF),intent(in) :: f
          character(len=*),intent(in) :: caller
          if (.not.is_collocated(f)) then
            call print(f%x%DL)
            call print(f%y%DL)
            call print(f%z%DL)
            write(*,*) 'Error: DLs are not collocated in '//caller//' in VF.f90'
            stop 'Done'
          endif
        end subroutine

