       use mesh_extend_mod
       use ops_embedExtract_mod
       use mesh_domain_extend_mod
       use SF_extend_mod
       implicit none

       private
       public :: initK

       integer :: preDefined_K = 1 ! k* = k_wall/k_l
       !                                  0 : User-defined case (no override)
       !                                  1 : k* = kStar
       real(cp) :: kStarWall = 1.0_cp ! k* = k_wall/k_l

