       subroutine initK(k,m,MD)
         implicit none
         type(mesh),intent(in) :: m
         type(SF),intent(inout) :: k
         type(mesh_domain),intent(in) :: MD
         if (preDefined_K.ne.0) then
           call initPredefinedK(k,m,MD)
         else
           call initUserK(k,m,MD)
         endif
