       subroutine Ln_vol_SF(e,u,n,vol)
         implicit none
         real(cp),intent(inout) :: e
         type(SF),intent(in) :: u,vol
         real(cp),intent(in) :: n
         real(cp) :: temp
         integer :: t
         e = 0.0_cp
         do t=1,u%s
