         type(SF),intent(in) :: edge
         type(mesh),intent(in) :: m
         integer,intent(in) :: faceDir,edgeDir
         integer :: orthDir
         if (edgeDir.ne.faceDir) then ! Requires 1 interpolation (no allocations)
           orthDir = orth_dir((/edgeDir,faceDir/))
