       subroutine compute_Q(Q,T,k,m)
         implicit none
         type(VF),intent(inout) :: Q
         type(SF),intent(in) :: T
         type(VF),intent(in) :: k
         type(mesh),intent(in) :: m
         call grad(Q,T,m)
         call multiply(Q,k)
