         call extractCC(field_domain,field,MD)
         call assign_ghost_XPeriodic(field_domain,0.0_cp)
         call compute_Ln(temp,field_domain,2.0_cp,m,MD)
         temp = scale*0.5_cp*temp
         call export(energy,TMP,temp)
