       subroutine compute_J_ind(ind,SP)
         implicit none
         type(induction),intent(inout) :: ind
         type(sim_params),intent(in) :: SP
         ! call assign(ind%J,0.0_cp)
         ! call embedEdge(ind%J,ind%J_interior,ind%MD_sigma)
